(article?){haun2018, title = {Effects of {Graded} {Whey} {Supplementation} {During} {Extreme}-{Volume} {Resistance} {Training}}, volume = {5}, issn = {2296-861X (Print) 2296-861x}, url = {https://www.ncbi.nlm.nih.gov/pmc/articles/PMC6141782/pdf/fnut-05-00084.pdf}, doi = {10.3389/fnut.2018.00084}, abstract = {We examined hypertrophic outcomes of weekly graded whey protein dosing (GWP) vs. whey protein (WP) or maltodextrin (MALTO) dosed once daily during 6 weeks of high-volume resistance training (RT). College-aged resistance-trained males (training age = 5 +/- 3 years; mean +/- SD) performed 6 weeks of RT wherein frequency was 3 d/week and each session involved 2 upper- and 2 lower-body exercises (10 repetitions/set). Volume increased from 10 sets/exercise (week 1) to 32 sets/exercise (week 6), which is the highest volume investigated in this timeframe. Participants were assigned to WP (25 g/d; n = 10), MALTO (30 g/d; n = 10), or GWP (25-150 g/d from weeks 1-6; n = 11), and supplementation occurred throughout training. Dual-energy x-ray absorptiometry (DXA), vastus lateralis (VL), and biceps brachii ultrasounds for muscle thicknesses, and bioelectrical impedance spectroscopy (BIS) were performed prior to training (PRE) and after weeks 3 (MID) and 6 (POST). VL biopsies were also collected for immunohistochemical staining. The GWP group experienced the greatest PRE to POST reduction in DXA fat mass (FM) (-1.00 kg, p {} 0.05), and a robust increase in DXA fat- and bone-free mass [termed lean body mass (LBM) throughout] (+2.93 kg, p {} 0.05). However, the MALTO group also experienced a PRE to POST increase in DXA LBM (+2.35 kg, p {} 0.05), and the GWP and MALTO groups experienced similar PRE to POST increases in type II muscle fiber cross-sectional area ({}+300 mum(2)). When examining the effects of training on LBM increases (DeltaLBM) in all participants combined, PRE to MID (+1.34 kg, p {} 0.001) and MID to POST (+0.85 kg, p {} 0.001) increases were observed. However, when adjusting DeltaLBM for extracellular water (ECW) changes, intending to remove the confounder of edema, a significant increase was observed from PRE to MID (+1.18 kg, p {} 0.001) but not MID to POST (+0.25 kg; p = 0.131). Based upon DXA data, GWP supplementation may be a viable strategy to improve body composition during high-volume RT. However, large LBM increases observed in the MALTO group preclude us from suggesting that GWP supplementation is clearly superior in facilitating skeletal muscle hypertrophy. With regard to the implemented RT program, ECW-corrected DeltaLBM gains were largely dampened, but still positive, in resistance-trained participants when RT exceeded {}20 sets/exercise/wk.}, language = {eng}, journal = {Front Nutr}, author = {Haun, C. T. and Vann, C. G. and Mobley, C. B. and Roberson, P. A. and Osburn, S. C. and Holmes, H. M. and Mumford, P. M. and Romero, M. A. and Young, K. C. and Moon, J. R. and Gladden, L. B. and Arnold, R. D. and Israetel, M. A. and Kirby, A. N. and Roberts, M. D.}, year = {2018}, note = {Edition: 2018/09/27}, keywords = {resistance training, adaptation, muscle hypertrophy, graded whey protein, recovery}, pages = {84}}, }
(article?){vickers2001, title = {The use of percentage change from baseline as an outcome in a controlled trial is statistically inefficient: a simulation study}, volume = {1}, issn = {1471-2288}, url = {https://www.ncbi.nlm.nih.gov/pmc/articles/PMC34605/pdf/1471-2288-1-6.pdf}, abstract = {BACKGROUND: Many randomized trials involve measuring a continuous outcome - such as pain, body weight or blood pressure - at baseline and after treatment. In this paper, I compare four possibilities for how such trials can be analyzed: post-treatment; change between baseline and post-treatment; percentage change between baseline and post-treatment and analysis of covariance (ANCOVA) with baseline score as a covariate. The statistical power of each method was determined for a hypothetical randomized trial under a range of correlations between baseline and post-treatment scores. RESULTS: ANCOVA has the highest statistical power. Change from baseline has acceptable power when correlation between baseline and post-treatment scores is high;when correlation is low, analyzing only post-treatment scores has reasonable power. Percentage change from baseline has the lowest statistical power and was highly sensitive to changes in variance. Theoretical considerations suggest that percentage change from baseline will also fail to protect from bias in the case of baseline imbalance and will lead to an excess of trials with non-normally distributed outcome data. CONCLUSIONS: Percentage change from baseline should not be used in statistical analysis. Trialists wishing to report this statistic should use another method, such as ANCOVA, and convert the results to a percentage change by using mean baseline scores.}, language = {eng}, journal = {BMC Med Res Methodol}, author = {Vickers, A. J.}, year = {2001}, note = {Edition: 2001/07/19}, keywords = {Humans, Models, Statistical, Computer Simulation/statistics & numerical data, Randomized Controlled Trials as Topic/statistics & numerical data, Research Design/statistics & numerical data, Software/statistics & numerical data}, pages = {6}, file = {Vickers-2001-The use of percentage change from:C:\Users\706194\Zotero\storage\HXX45XYR\Vickers-2001-The use of percentage change from.pdf:application/pdf}, }
(article?){lakens2022, title = {Sample {Size} {Justification}}, volume = {8}, issn = {2474-7394}, url = {https://online.ucpress.edu/collabra/article/8/1/33267/120491/Sample-Size-Justification}, doi = {10.1525/collabra.33267}, abstract = {An important step when designing an empirical study is to justify the sample size that will be collected. The key aim of a sample size justification for such studies is to explain how the collected data is expected to provide valuable information given the inferential goals of the researcher. In this overview article six approaches are discussed to justify the sample size in a quantitative empirical study: 1) collecting data from (almost) the entire population, 2) choosing a sample size based on resource constraints, 3) performing an a-priori power analysis, 4) planning for a desired accuracy, 5) using heuristics, or 6) explicitly acknowledging the absence of a justification. An important question to consider when justifying sample sizes is which effect sizes are deemed interesting, and the extent to which the data that is collected informs inferences about these effect sizes. Depending on the sample size justification chosen, researchers could consider 1) what the smallest effect size of interest is, 2) which minimal effect size will be statistically significant, 3) which effect sizes they expect (and what they base these expectations on), 4) which effect sizes would be rejected based on a confidence interval around the effect size, 5) which ranges of effects a study has sufficient power to detect based on a sensitivity power analysis, and 6) which effect sizes are expected in a specific research area. Researchers can use the guidelines presented in this article, for example by using the interactive form in the accompanying online Shiny app, to improve their sample size justification, and hopefully, align the informational value of a study with their inferential goals.}, language = {en}, number = {1}, urldate = {2022-04-18}, journal = {Collabra: Psychology}, author = {Lakens, Daniël}, month = mar, year = {2022}, pages = {33267}, file = {Fulltekst:C:\Users\706194\Zotero\storage\C457SA9E\Lakens - 2022 - Sample Size Justification.pdf:application/pdf}, }
(techreport?){bryan_2017, type = {preprint}, title = {Excuse me, do you have a moment to talk about version control?}, url = {https://peerj.com/preprints/3159v2}, abstract = {Data analysis, statistical research, and teaching statistics have at least one thing in common: these activities all produce many files! There are data files, source code, figures, tables, prepared reports, and much more. Most of these files evolve over the course of a project and often need to be shared with others, for reading or edits, as a project unfolds. Without explicit and structured management, project organization can easily descend into chaos, taking time away from the primary work and reducing the quality of the final product. This unhappy result can be avoided by repurposing tools and workflows from the software development world, namely, distributed version control. This article describes the use of the version control system Git and and the hosting site GitHub for statistical and data scientific workflows. Special attention is given to projects that use the statistical language R and, optionally, R Markdown documents. Supplementary materials include an annotated set of links to step-by-step tutorials, real world examples, and other useful learning resources.}, language = {en}, urldate = {2022-09-20}, institution = {PeerJ Preprints}, author = {Bryan, Jennifer}, month = aug, year = {2017}, doi = {10.7287/peerj.preprints.3159v2}, file = {Versjon sendt inn:C:\Users\706194\Zotero\storage\GTWIYRDF\Bryan - 2017 - Excuse me, do you have a moment to talk about vers.pdf:application/pdf}, }
(book?){campbell_2020, address = {Hoboken, NJ}, edition = {Fifth edition}, title = {Medical statistics: a textbook for the health sciences}, isbn = {978-1-119-42364-5}, shorttitle = {Medical statistics}, abstract = {“The fifth edition of this popular textbook provides students and practitioners with a clear, concise introduction to the statistics they will come across in their regular reading of clinical papers. Written by three experts with wide teaching and consulting experience, Medical Statistics: A Textbook for the Health Sciences, 5th Edition: Assumes no prior knowledge of statistics. Covers all essential statistical methods. Completely revised, updated and expanded. Includes numerous examples and exercises on the interpretation of the statistics in papers published in medical journals. Includes a companion website with downloadable data, new separate chapters on topics such as logistic regression; diagnostic tests; assessing the quality of statistics/reading the statistics in a scientific paper and other statistical methods (such as meta-analysis; the bootstrap; imputation of missing data and summary measures for analysing longitudinal data) and additional sections on designs such as pilot/feasibility studies; cluster and stepped wedge randomised controlled trials”–}, publisher = {Wiley-Blackwell}, author = {Campbell, Michael J. and Walters, Stephen John and Machin, David}, year = {2020}, keywords = {Biometry, Statistics as Topic, Research Design, methods}, }
