(article?){Ost, title = “The Effect of Weight Training Volume on Hormonal Output and Muscular Size and Function”, author = “Ostrowski, Karl J and Wilson, Greg J and Weatherby, Robert and Murphy, Peter W and Lyttle, Andrew D”, abstract = “This study examined the effects of different volumes of resistance training on muscle size and function over a 10-wk period. Low volume = 3 sets per muscle group per week; moderate = 6 sets; high = 12 sets. Twenty-seven men with 1–4 yrs weight training experience were randomly assigned to the different training volumes and trained 4 days a week. A periodized routine was used; exercises, training intensity, and number of training days were the same for each group. The only variation between conditions was the number of sets per exercise. Pre and post measurements assessed muscular size via ultrasound; strength via maximum squat and bench press; and power via vertical jump and bench press throw. Urinary concentrations of test-osterone and cortisol were also analyzed to assess the responses to training conditions. All 3 training volumes significantly (p &lt; 0.05) increased muscle size, strength, and upper body power, with no significant between-group differences. There were no significant changes in hormonal concentrations. The results support the use of low volume training for muscular development over a 10-wk period.”, journal = “The Journal of Strength & Conditioning Research”, volume = 11, number = 3, pages = “148–154”, year = 1997, keywords = “strength training; hormonal responses; muscular development” }
(article?){Cannon, title = “Early-phase neuromuscular adaptations to high- and low-volume resistance training in untrained young and older women”, author = “Cannon, Jack and Marino, Frank E”, abstract = “We compared early-phase effects between high- and low-volume moderate-intensity resistance training on lean muscle volume, maximal bilateral leg extension strength, maximal isometric torque, normalized maximal bilateral leg extension strength, normalized maximal isometric torque, and muscle recruitment of the right knee extensors in previously untrained young (23.8 \(\pm\) 3.7 years, range 20-30 years; n = 16) and older women (67.6 \(\pm\) 6.3 years, range 60-78 years; n = 15). Participants performed either one set or three sets of 10 repetitions for the bilateral leg extension and bilateral leg curl at an intensity of 50-75% of maximal strength 3 days per week for 10 weeks. Main effects were observed over time for all variables (P 0.05). Our results provide a novel contribution to the literature demonstrating that additional neuromuscular adaptation during early-phase moderate-intensity resistance training in previously untrained young and older women may not be elicited through higher-volume training when training loads are matched provided that a minimal volume threshold is attained. These findings may have practical applications for the prescription of short-duration resistance training programmes to enhance muscle strength and achieve hypertrophic and non-hypertrophic adaptations in untrained women.”, journal = “J. Sports Sci.”, publisher = “Informa UK Limited”, volume = 28, number = 14, pages = “1505–1514”, month = dec, year = 2010, language = “en” }
(article?){Galvao, title = “Resistance exercise dosage in older adults: single- versus multiset effects on physical performance and body composition”, author = “Galv{~a}o, Daniel A and Taaffe, Dennis R”, abstract = “OBJECTIVES: To determine whether variation in resistance exercise volume affects muscle function and physical performance response in older adults. DESIGN: A randomized trial with subjects assigned to a single-set (1-SET) or three-set (3-SET) exercise group. SETTING: An exercise facility at the University of Queensland. PARTICIPANTS: Twenty-eight community-dwelling men and women aged 65 to 78. INTERVENTION: Progressive resistance training consisting of seven exercises targeting the major muscle groups of the upper and lower body performed on exercise machines twice weekly for 20 weeks at eight-repetition maximum (RM) intensity. MEASUREMENTS: Muscle function included isotonic muscle strength (1-RM) of the seven exercises, isokinetic and isometric knee extensor strength, and muscle endurance for the chest press and leg press exercises. Physical performance included timed chair rise, usual and fast 6-m walk, 6-m backwards walk, 400-m walk, floor rise to standing, and stair climbing ability. In addition, body composition was determined using dual energy x-ray absorptiometry. RESULTS: Isotonic muscle strength increased in both exercise groups for all seven exercises (P&lt;.01), with the gain in the 3-SET group greater (P&lt;.05) for the seated row, triceps extension, and knee extension (analysis of covariance). Similarly, muscle endurance gains were greater for the 3-SET than the 1-SET group (P&lt;.01), with no significant difference between groups for isokinetic and isometric knee extensor strength. Both groups improved (P&lt;.05) in the chair rise (1-SET, 10.1%; 3-SET, 13.6%), 6-m backwards walk (1-SET, 14.3%; 3-SET, 14.8%), 400-m walk (1-SET, 3.8%; 3-SET, 7.4%), and stair climbing test (1-SET, 7.7%; 3-SET, 6.4%), with the only difference between groups for the 400-m walk (P&lt;.05). There was no difference between groups for change in body composition. CONCLUSION: Resistance training consisting of only single-set exercises is sufficient to significantly enhance muscle function and physical performance, although muscle strength and endurance gains are greater with higher-volume work. These findings have application in designing time-efficient exercise regimens to enhance neuromuscular function in older adults.”, journal = “J. Am. Geriatr. Soc.”, publisher = “Wiley”, volume = 53, number = 12, pages = “2090–2097”, month = dec, year = 2005, copyright = “http://onlinelibrary.wiley.com/termsAndConditions#vor”, language = “en” }
(article?){Humburg, title = “1-Set vs. 3-set resistance training: a crossover study”, author = “Humburg, Hartmut and Baars, Hartmut and Schr{"o}der, Jan and Reer, R{"u}diger and Braumann, Klaus-Michael”, abstract = “This crossover study was conducted to investigate the effects of a 1-set and 3-set strength training program. The subjects were untrained men and women who were randomly signed into 1 of 3 groups: 10 subjects trained during the first 9 weeks (training period 1) with 1 set and 8-12 repetitions per set. After the break (9 weeks), they trained with 3 sets and 8-12 repetitions in training period 2. Twelve subjects started with the 3-set program and continued with the 1-set regime after the break. The control group (n = 7) did not train. The subjects were tested on 1 repetition maximum (1RM) for the biceps curl, leg press (unilateral: left and right), and bench press. Analysis of the data was done in a sampled manner for each strength training program (1-set and 3-set). The 1-set (n = 22) and 3-set (n = 22) programs led to significantly (p &lt; 0.05) improved 1RM performances in every exercise. The relative improvements (%) for the 1RM were significantly higher during the 3-set program for the biceps curl and the bench press compared with the 1-set program. The control group exhibited no changes in any of the tested parameters over the course of this study. The design of this study allowed insight into the effects of different strength training volume without any genetical variations. The same subjects improved their 1RM during the 3-set program by 2.3 kg (biceps curl; corresponding effect size = 0.24), 8.9 kg (leg press right; 0.30), 10.9 kg (leg press left; 0.28), and 2.5 kg (bench press; 0.09) more than during the 1-set program. Depending on the goals of each trainee, these differences between the effects of different strength training volumes indicate that it may be worth spending more time on working out with a 3-set strength training regime.”, journal = “J. Strength Cond. Res.”, publisher = “Ovid Technologies (Wolters Kluwer Health)”, volume = 21, number = 2, pages = “578–582”, month = may, year = 2007, language = “en” }
(article?){Kraemer, title = “Understanding the science of resistance training: An evolutionary perspective”, author = “Kraemer, William J and Ratamess, Nicholas A and Flanagan, Shawn D and Shurley, Jason P and Todd, Janice S and Todd, Terry C”, abstract = “The history of resistance training research began with anecdotal ideas and a slow growth of research from the late 1890s through the 1970s. The mid-1970s were a nexus point when resistance training studies evolved from just strength assessments to importance in physiological systems, physical health, and physical performance capabilities for individuals interested in physical fitness through to those seeking elite athletic performances. The pursuit of understanding program design and what mediated successful programs continues today as new findings, replication of old concepts, and new visions with the latest technologies fuel both our understanding and interest in this modality. This brief review highlights some of the important scientific contributions to the evolution of our scientific study of resistance training and provides a literature base analysis for greater quantification of the origins and expanse of such investigations.”, journal = “Sports Med.”, publisher = “Springer Science and Business Media LLC”, volume = 47, number = 12, pages = “2415–2435”, month = dec, year = 2017, language = “en” }
(article?){McBride, title = “Effect of resistance exercise volume and complexity on {EMG}, strength, and regional body composition”, author = “McBride, Jeffrey M and Blaak, John B and Triplett-McBride, Travis”, abstract = “This study was designed to examine the effects of a 12-week resistance training program using single versus multiple sets of a complex versus simple exercise on EMG, strength and regional body composition. Twenty-eight untrained men ( n=15) and women ( n=13) performed resistance training twice per week. Group 1 (S-1, n=9) performed one set of a leg press (LP) and bicep curl (BC) exercise, group 2 (M-6, n=9) performed six sets of a LP and BC exercise, and group 3 (C, n=10) was the control group. One-repetition maximums (1RMs) and EMG were measured in the LP and BC during pre-, mid-, and post-training. Lean body mass of the legs and arms were measured pre- and post-training by dual energy X-ray absorptiometry. Results of the study indicated that both S-1 and M-6 groups significantly increased percentage strength pre- to post-training in both the LP and BC [S-1 pre-/post-LP=41.2 (23.7)%, BC=8.5 (6.71)%; M-6 pre-/post-LP=52.6 (12.6)%, BC=22.8 (15.6)%; mean (SD)]. However, compared to S-1, M-6 showed a significantly greater increase in percentage strength in the BC ( P&lt;or=0.05) from pre- to post-testing. Results also showed that M-6 produced a significant increase in biceps (single-joint) muscle EMG values pre- to mid-testing compared to S-1. There were no significant differences found in lean muscle mass gains for the legs or arms in either training group. The data from this study suggest that multiple sets produce a greater increase in percentage strength gain for a simple exercise. It is possible that some type of neural mechanism is responsible for the observed difference.”, journal = “Eur. J. Appl. Physiol.”, publisher = “Springer Science and Business Media LLC”, volume = 90, number = “5-6”, pages = “626–632”, month = nov, year = 2003, language = “en” }
(article?){Berger, title = “Effect of varied weight training programs on strength”, author = “Berger, Richard”, abstract = “Nine different weight training programs were compared to determine which were more effective in improving strength. The experiment was conducted with the bench press lift for a period of 12 weeks with approximately 20 subjects in each weight training program. Subjects were tested for the 1 RM on the bench press lift at the beginning of training and at three-week intervals. Training took place three times weekly with the variations in programs involving one, two, and three sets, and two, six, and ten repetitions per set. The results showed that three sets and six repetitions per set were best for improving strength.”, journal = “Res. Q. Am. Assoc. Health Phys. Educ. Recreat.”, publisher = “Informa UK Limited”, volume = 33, number = 2, pages = “168–181”, month = may, year = 1962 }
